mapper.select:
SELECT COUNT(*)
FROM goods
WHERE member_id = #{memberId}

mapper.update:
UPDATE goods
SET    name = #{name},
	   description = #{description},
	   image = #{image},
	   price = #{price}
WHERE goods_id = #{goodsId}

UPDATE goods
SET    goods_state = #{goodsState}
WHERE  goods_id = #{goodsId}
